  ) {
    this._channel = channel;
    this.watcher_count = 0;
    this.typing = Immutable<{
      [key: string]: Immutable.Immutable<
        Event<
          AttachmentType,
          ChannelType,
          CommandType,
          EventType,
          MessageType,
          ReactionType,
          UserType
        >
      >;
    }>({});
    this.read = Immutable<{
      [key: string]: Immutable.Immutable<{
        last_read: Date;
        user: UserResponse<UserType>;
      }>;
    }>({});
    this.messages = Immutable([]);
    this.pinnedMessages = Immutable([]);
    this.threads = Immutable<{
