    eventHandlers.onUserClickHandler(event, message.user);
  },
  onUserHover: (event) => {
    if (
      typeof eventHandlers?.onUserHoverHandler !== 'function' ||
      !message?.user
    ) {
      return;
    }

    eventHandlers.onUserHoverHandler(event, message.user);
  },
});
