import { MAX_QUERY_CHANNELS_LIMIT } from '../utils';

import type {
  Channel,
  ChannelFilters,
  ChannelOptions,
  ChannelSort,
  StreamChat,
} from 'stream-chat';

import type {
  DefaultAttachmentType,
  DefaultChannelType,
